        // Wait between bursts
        if (burst < numBursts - 1) {
          await new Promise(resolve => setTimeout(resolve, burstInterval));
        }
      }
    });
  });

  describe('Performance Monitoring', () => {
    it('should track performance metrics', async () => {
      const metrics = {
        totalRequests: 0,
        totalDuration: 0,
        minDuration: Infinity,
