import bcrypt from 'bcrypt';
import jwt from 'jsonwebtoken';
import { v4 as uuidv4 } from 'uuid';
import { DatabaseService } from '../services/database.service';
import { EmailService } from '../services/email.service';
import { CacheService } from '../services/cache.service';
import { logger } from '../utils/logger';

export interface User {
  id: string;
  email: string;
  username: string;
  passwordHash: string;
  firstName?: string;
  lastName?: string;
  phone?: string;
  avatarUrl?: string;
  bio?: string;
  isActive: boolean;
  isVerified: boolean;
  emailVerified: boolean;
  createdAt: Date;
  updatedAt: Date;
}

export interface Session {
  id: string;
  userId: string;
  refreshToken: string;
  accessToken: string;
  deviceInfo: any;
  ipAddress: string;
  userAgent: string;
  expiresAt: Date;
  isActive: boolean;
  createdAt: Date;
  lastUsed: Date;
}

export class AuthService {
  private db: DatabaseService;
  private emailService: EmailService;
  private cacheService: CacheService;
  private saltRounds: number = 12;

  constructor() {
    this.db = new DatabaseService();
    this.emailService = new EmailService();
    this.cacheService = new CacheService();
  }

  /**
   * Register a new user
   */
  public async register(userData: {
    email: string;
    username: string;
    password: string;
    firstName?: string;
    lastName?: string;
    phone?: string;
  }): Promise<{
    user: Omit<User, 'passwordHash'>;
    accessToken: string;
    refreshToken: string;
  }> {
    // Check if user already exists
    const existingUser = await this.db.query(
      'SELECT id, email, username FROM users WHERE email = $1 OR username = $2',
      [userData.email, userData.username]
    );

    if (existingUser.rows.length > 0) {
      throw new Error('User with this email or username already exists');
    }

    // Hash password
    const passwordHash = await bcrypt.hash(userData.password, this.saltRounds);

    // Create user
    const userResult = await this.db.query(
      `INSERT INTO users (id, email, username, password_hash, first_name, last_name, phone)
       VALUES ($1, $2, $3, $4, $5, $6, $7)
       RETURNING id, email, username, first_name, last_name, phone, avatar_url, bio, is_active, is_verified, email_verified, created_at, updated_at`,
      [
        uuidv4(),
        userData.email,
        userData.username,
        passwordHash,
        userData.firstName,
        userData.lastName,
        userData.phone
      ]
    );

    const user = userResult.rows[0];

    // Assign default role
    await this.db.query(
      `INSERT INTO user_roles (id, user_id, role_id, assigned_by)
       VALUES ($1, $2, (SELECT id FROM roles WHERE name = 'user'), $3)`,
      [uuidv4(), user.id, user.id]
    );

    // Generate JWT tokens
    const { accessToken, refreshToken } = await this.generateTokens(user.id);

    // Create session
    await this.createSession(user.id, accessToken, refreshToken, {
      userAgent: 'registration',
      ipAddress: 'unknown'
    });

    // Send verification email
    await this.sendVerificationEmail(user.id, user.email);

    // Log registration
    logger.info('User registered', {
      userId: user.id,
      email: user.email,
      username: user.username
    });

    return {
      user: {
        id: user.id,
        email: user.email,
        username: user.username,
        firstName: user.first_name,
        lastName: user.last_name,
        phone: user.phone,
        avatarUrl: user.avatar_url,
        bio: user.bio,
        isActive: user.is_active,
        isVerified: user.is_verified,
        emailVerified: user.email_verified,
        createdAt: user.created_at,
        updatedAt: user.updated_at
      },
      accessToken,
      refreshToken
    };
  }

  /**
   * Login user
   */
  public async login(credentials: {
    email: string;
    password: string;
    rememberMe?: boolean;
    userAgent?: string;
    ipAddress?: string;
  }): Promise<{
    user: Omit<User, 'passwordHash'>;
    accessToken: string;
    refreshToken: string;
  }> {
    // Find user
    const userResult = await this.db.query(
      'SELECT id, email, username, password_hash, first_name, last_name, phone, avatar_url, bio, is_active, is_verified, email_verified, created_at, updated_at FROM users WHERE email = $1',
      [credentials.email]
    );

    if (userResult.rows.length === 0) {
      throw new Error('Invalid credentials');
    }

    const user = userResult.rows[0];

    // Check if user is active
    if (!user.is_active) {
      throw new Error('Account is deactivated');
    }

    // Verify password
    const isPasswordValid = await bcrypt.compare(credentials.password, user.password_hash);
    if (!isPasswordValid) {
      throw new Error('Invalid credentials');
    }

    // Generate JWT tokens
    const { accessToken, refreshToken } = await this.generateTokens(
      user.id,
      credentials.rememberMe ? '30d' : '1h'
    );

    // Create session
    await this.createSession(user.id, accessToken, refreshToken, {
      userAgent: credentials.userAgent,
      ipAddress: credentials.ipAddress
    });

    // Update last login
    await this.db.query(
      'UPDATE users SET last_login = CURRENT_TIMESTAMP WHERE id = $1',
      [user.id]
    );

    // Log login
    logger.info('User logged in', {
      userId: user.id,
      email: user.email,
      ipAddress: credentials.ipAddress
    });

    return {
      user: {
        id: user.id,
        email: user.email,
        username: user.username,
        firstName: user.first_name,
        lastName: user.last_name,
        phone: user.phone,
        avatarUrl: user.avatar_url,
        bio: user.bio,
        isActive: user.is_active,
        isVerified: user.is_verified,
