      }

      // Generate new tokens
      const { accessToken, refreshToken: newRefreshToken } = await this.generateTokens(
        session.user_id
      );

      // Update session with new tokens
      await this.db.query(
        'UPDATE user_sessions SET access_token = $1, refresh_token = $2, last_used = CURRENT_TIMESTAMP WHERE id = $3',
