   */
  public async cleanupExpiredSessions(): Promise<void> {
    await this.db.query(
      'UPDATE user_sessions SET is_active = false WHERE expires_at < CURRENT_TIMESTAMP'
    );

    logger.info('Expired sessions cleaned up');
  }
}

