          message: 'Too many requests from this IP, please try again later'
        },
        meta: {
          timestamp: new Date().toISOString(),
          request_id: uuidv4()
        }
      },
      standardHeaders: true,
      legacyHeaders: false,
      handler: (req, res) => {
        logger.warn('Rate limit exceeded', {
          ip: req.ip,
          path: req.path,
          userAgent: req.get('User-Agent')
        });

        res.status(429).json({
          success: false,
          error: {
            code: 'TOO_MANY_REQUESTS',
            message: 'Too many requests from this IP, please try again later'
          },
          meta: {
            timestamp: new Date().toISOString(),
            request_id: uuidv4()
          }
        });
      }
    });

    // Auth endpoints rate limiter (more strict)
    const authLimiter = rateLimit({
      windowMs: 15 * 60 * 1000, // 15 minutes
      max: 5, // limit each IP to 5 auth requests per windowMs
      message: {
        success: false,
        error: {
          code: 'TOO_MANY_REQUESTS',
          message: 'Too many authentication attempts, please try again later'
        },
        meta: {
          timestamp: new Date().toISOString(),
          request_id: uuidv4()
        }
      },
      skipSuccessfulRequests: true
    });

    return (req: Request, res: Response, next: NextFunction) => {
      // Apply stricter rate limiting to auth endpoints
      if (req.path.startsWith('/auth/') || req.path.startsWith('/api-keys/')) {
        return authLimiter(req, res, next);
      }

      return generalLimiter(req, res, next);
    };
  }

  /**
   * XSS Protection
   */
  private static xssProtection() {
    return (req: Request, res: Response, next: NextFunction) => {
      res.setHeader('X-XSS-Protection', '1; mode=block');
      next();
    };
  }

  /**
   * No Sniff
   */
  private static noSniff() {
    return (req: Request, res: Response, next: NextFunction) => {
      res.setHeader('X-Content-Type-Options', 'nosniff');
      next();
    };
  }

  /**
   * Frameguard
   */
  private static frameguard() {
    return (req: Request, res: Response, next: NextFunction) => {
      res.setHeader('X-Frame-Options', 'SAMEORIGIN');
      next();
    };
  }

  /**
   * HSTS
   */
  private static hsts() {
    return (req: Request, res: Response, next: NextFunction) => {
      if (req.secure) {
        res.setHeader(
          'Strict-Transport-Security',
          'max-age=31536000; includeSubDomains; preload'
        );
      }
      next();
    };
  }

  /**
   * Content Type Options
   */
  private static contentTypeOptions() {
    return (req: Request, res: Response, next: NextFunction) => {
      res.setHeader('X-Content-Type-Options', 'nosniff');
      next();
    };
  }

  /**
