          errors.push({ field: fieldPath, message: `${field} must be no more than ${rules.max} characters long` });
        }
        if (rules.pattern && !rules.pattern.test(value)) {
          errors.push({ field: fieldPath, message: `${field} format is invalid` });
        }
      }

