      }
    ];
  }

  async getTestStats() {
    const stats = {};

    // PostgreSQL stats
    if (this.testPostgresPool) {
      try {
        const datasetCount = await this.testPostgresPool.query('SELECT COUNT(*) FROM datasets');
        const correlationCount = await this.testPostgresPool.query('SELECT COUNT(*) FROM correlations');
